// Ty for shader(by katila4) 
// https://www.shadertoy.com/view/3lXcR7
// I took the shader here ready for the game and edited it. 
// You can use it as glsl or hlsl if you want.

float Grid(in vec2 pos)
{
    return step(0.2, mod(pos.y * 100.0, 0.75));
}

float Intensivity(in vec2 orig)
{
    return 0.299 * orig.x + 0.587 * orig.y;
}

vec3 ColorChanges(in vec2 uv, vec3 color)
{
    vec2 orig = uv;   
    uv.y += 0.5 * iTime;  
    float resI = (Grid(uv) * (max(Intensivity(orig), 0.55) - 0.05) * 1.55);
    return vec3(resI) * color * 2.15;
}

vec2 Line(in vec2 pos)
{
    float v = sin((pos.y + iTime * 4.0) * 3.14 * 4.0);
    pos.x += 0.0015 * sin(v * 3.14) * cos(v * 3.14 * 2.0);
    pos.y += 0.0055 * cos(v * 3.14) * sin(v * 3.14 * 2.0);
    return pos;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = Line(fragCoord/iResolution.xy);
    vec3 color = vec3(0.,1.,1.);
    vec3 resCol = ColorChanges(uv, color);
    fragColor = vec4(resCol, 1.0);
}

