// Fork of "Buffers (API Test)" by brisingre. https://shadertoy.com/view/4XdGRX
// 2024-05-29 16:46:43

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2.0;


    float fade = max(sin(iTime), 0.0);

    vec4 color;

    if(uv.x < 1.0)
    {
        if(uv.y < 1.0)
        {
            color = texture(iChannel2, fract(uv));
        }
        else
        {
            color = texture(iChannel0, fract(uv));

        }
    }
    else
    {
        if(uv.y < 1.0)
        {
            color = texture(iChannel3, fract(uv));
        }
        else
        {
            color = texture(iChannel1, fract(uv));

        }
    }


    fragColor = color;
}

//CIRCULAR ACCUMULATION


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 texColor = texture(iChannel0, uv);

    vec2 point = vec2(sin(iTime), cos(iTime))/3.0 + vec2(0.5);
    float sdf = step(distance(uv, point), 0.2);

    vec3 color = palette(iTime/5.0, vec3(0.5, 0.5, 0.5), vec3(0.5, 0.5, 0.5), vec3(1.0, 1.0, 1.0), vec3(0.00, 0.10, 0.20));
    fragColor = mix(texColor, vec4(color, 1.0), sdf);
}

