// ShaderToy GLSL fragment shader

// Predefined set of rainbow colors
vec3 getColor(int index, float time) {
    vec3 colors[7];
    colors[0] = vec3(1.0, 0.0, 0.0);   // Red
    colors[1] = vec3(1.0, 0.5, 0.0);   // Orange
    colors[2] = vec3(1.0, 1.0, 0.0);   // Yellow
    colors[3] = vec3(0.0, 1.0, 0.0);   // Green
    colors[4] = vec3(0.0, 0.0, 1.0);   // Blue
    colors[5] = vec3(0.29, 0.0, 0.51); // Indigo
    colors[6] = vec3(0.56, 0.0, 1.0);  // Violet

    // Cycle through colors over time
    float speed = 0.2; // Adjust speed of color change
    float phase = mod(time * speed + float(index), 7.0);
    int i0 = int(floor(phase));
    int i1 = (i0 + 1) % 7;
    float t = phase - float(i0);

    // Interpolate between colors
    return mix(colors[i0], colors[i1], t);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalize the pixel coordinates to the range [0, 1]
    vec2 uv = fragCoord / iResolution.xy;

    // Time factor for animation
    float time = iTime * 0.2;

    // Calculate the band index
    int bandIndex = int(uv.y * 7.0);

    // Get the current and next colors for the gradient transition
    vec3 color1 = getColor(bandIndex, time);
    vec3 color2 = getColor(bandIndex + 1, time);

    // Create a unique gradient for each band by adding the band index to the time factor
    float gradient = abs(sin((uv.x + time + float(bandIndex) * 1.5) * 0.5));

    // Interpolate between the two colors based on the gradient
    vec3 color = mix(color1, color2, gradient);

    // Output the final color with the gradient applied
    fragColor = vec4(color, 1.0);
}
