#define NITER 800

// can also be written mat2(a, -a.y,a.x) *b  , by demoivre formula
vec2 cmul(vec2 a, vec2 b) {
        return vec2(a.x*b.x - a.y*b.y, a.y*b.x + a.x*b.y);
}

vec2 f(vec2 z, vec2 c) {
    return cmul(z,z) + c;
}

vec2 getz_mandelbrot(vec2 fragCoord) {
    vec2 half_stretch_ndc = (fragCoord - .5*iResolution.xy) / iResolution.yy;
    vec2 uv = half_stretch_ndc + vec2(-0.75, 0.5);
    return uv * 3.0;
}

vec2 getz_julia(vec2 fragCoord) {
    vec2 half_stretch_ndc = (fragCoord - .5*iResolution.xy) / iResolution.yy;
    //vec2 uv = half_stretch_ndc + vec2(-0.5, 0.5);
    return half_stretch_ndc * 3.0;
}

// n iters
int mandelbrot(vec2 z) {
    vec2 acc = z;
    const int niter = 150;
    for (int i = 0; i < NITER; i++) {
        acc = f(acc, z);
        if (dot(acc,acc) >= 4.0) {
            return i;
        }
    }
    return -1;
}


// n iters
int julia(vec2 z) {
    vec2 acc = z;
    const int niter = 150;
    for (int i = 0; i < NITER; i++) {
        acc = f(acc, getz_mandelbrot(iMouse.xy));
        if (dot(acc,acc) >= 4.0) {
            return i;
        }
    }
    return 0;
}

float channel(int  steps) {
    float t = float(steps) / float(NITER);
    t = sqrt(t);
    t = pow(t, 1.0/3.0);
    return t;
}

vec4 colour(int steps) {
    float t = float(steps) / float(NITER);
    t = pow(t, 1.0/5.0);
    return vec4(t);
}

// what if we blend colour channels offset by aa, would we get truetype

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float r = channel(julia(getz_julia(fragCoord)));
    float g = channel(julia(getz_julia(fragCoord + vec2(0.5, 0.0))));
    float b = channel(julia(getz_julia(fragCoord + vec2(0.0, 0.5))));
    vec4 cj = vec4(r, g, b, 1.0);

    //float rm = colour(mandelbrot(getz_mandelbrot(fragCoord))));
    //float gm = colour(mandelbrot(getz_mandelbrot(fragCoord))));
    vec4 cm = colour(mandelbrot(getz_mandelbrot(fragCoord)));
    fragColor = cm * 0.5;
    fragColor += cj;
    //fragColor = vec4(cm.x, 0.0, cj.x, 1.0);
}
