#define A s * sin(floor(iTime//
#define F abs(u.y + (s=-s) * .05                  \
                  - s * min(A)-1.) + 4.*f*f,      \
                        min(A)   ),               \
                            A)+1.) + 4.*--f*f++)) \
             )

void mainImage( out vec4 o, vec2 u )
{
    o.xyz = iResolution;

    float f = fract(iTime),
          s = 1.;

    o = vec4(max(abs(u = (u+u-o.xy)/o.y/.8).x,
                 min(F, F)) < .05 
          ^^ u.y > A+u.x)));
}

