//ToDo: Compare with oklab predicted lightness

//wavelengths in nm
const float uv = 350.0; //"ultraviolet"
const float ir = 600.0; //"infrarred"

vec3 gauss(vec3 x){
    //base 2 and ignore variance for performance
    return exp2(-x*x);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 gp = fragCoord/iResolution.xy;
    vec2 grid = vec2(2.0,2.0);
    vec2 p = fract(grid*gp);
    float lambda = mix(ir, uv, p.x);
    vec3 peak = (gp.y > 0.5)?
        vec3(550.0, 500.0, 450.0):
        vec3(550.0, 500.0, 400.0);
    vec3 spread = (gp.x > 0.5)?
        vec3(75.0):
        vec3(50.0);
    vec3 hue = (lambda-peak)/spread;
    vec3 col = gauss(hue);
    col = clamp(2.0*p.y,0.0,1.0)*col;
    fragColor = vec4(col,1.0);
}
