uvec2 pcg2d(uvec2 v)
{
    v = v * 1664525u + 1013904223u;

    v.x += v.y * 1664525u;
    v.y += v.x * 1664525u;

    v = v ^ (v>>16u);

    v.x += v.y * 1664525u;
    v.y += v.x * 1664525u;

    v = v ^ (v>>16u);

    return v;
}
vec2 pcg2d_f(vec2 v)
{
    return (1.0/float(0xffffffffu)) * vec2(pcg2d( uvec2(floatBitsToUint(v.x), floatBitsToUint(v.y)) ));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy -.5* iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.);
    float bpm = iTime*140./60.*.25;
    for(float i=0.,im=32.*4.;i<im;i++){
        float r = i/im;
        float rr = r*6.28;
        vec2 p= uv;
        vec2 rnd = (pcg2d_f(vec2(r)));
         p.x += r*1.3-.6;
        p.y += +cos(rr+bpm+rnd.y)*.33;
        float d = length(p)-.005;

        vec3 col_a = vec3(1.)*(.001+.005*abs(sin(rr+bpm))/(.001+max(0.,d)));
        vec3 col_b = vec3(1.)*smoothstep(0.005,0.001,abs(d)-.01);
        col += col_a*mix(vec3(.1,.5,.9),vec3(.9,.3,.1),sin(rr*40.+bpm*4.)*2.)/4.;
    }
    fragColor = vec4(sqrt(col),1.0);
}

vec2 mainSound( int samp, float time )
{
    vec2 d = vec2(0.);;
    for(float i=0.,im=8.;i++<im;){
     d += sin(6.2831*(440.0*i/10.)*time+i+sin(i+time))/im;
    }
    d = mix(sin(6.2831*110.*time)*vec2(1.),d,cos(time)*.5+.5);
    return tanh(d);
}
