#define L(x, y) length(uv - vec2(x, y))

float sc(vec2 uv, float t) {
  float c = cos(t);
  float s = sin(t);

  return smoothstep(
    1., .8,
    L(c, 0) *
    L(0, s) *
    L(c, s) *
    L(c * 2., sin(t * 3.) * .75)
  );
}

void mainImage(out vec4 O, in vec2 U) {
  vec2 uv = (U * 2. - iResolution.xy) / iResolution.y * 1.5;
  vec3 a = vec3(sc(uv, iTime));
  vec3 b = vec3(sc(uv, iTime + .01));

  O = vec4(mix(a, 1. - b, .5), 1);
}
