// Fork of "Mandelbrot cross product formula" by IgorZdrowowicz. https://shadertoy.com/view/lXSBRt
// 2024-10-25 12:57:00

// by Igor Zdrowowicz
// Welcome to my little Mandelbrot formula. Not sure how but apparently 
// cross producing z with 2,2,1 and 0.5,0.5,-1 consecutively 
// before squaring it etc., gives us this pretty boi. 
// Maybe there is some insight (aside from it coming out tilted by 45*) in this formula... 
// Let me know!
const float pi=3.14159;

vec2 tiltPt(vec2 p,float r){
    float t=atan(p.y/p.x)+r;
    if(p.x<0.){t+=pi;}
    return length(p)*vec2(cos(t),sin(t));}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = (fragCoord.xy / iResolution.xy) * 1.2 -.4;
    uv.x *= iResolution.x / iResolution.y; 

    float sc = 3.;
    vec2 os = vec2(-1.,0.0);
    uv = uv * sc + os;

    float col=0.;

    vec2 angle;
    if (iMouse.z>0.){
        angle=pi*((2.*iMouse.xy/iResolution.xy)-1.);}
    else{
        angle=vec2(iTime,iTime/3.);}

    for(float i=2.5;i>-2.5 && col==0.;i-=1./200.){
        vec2 zr=tiltPt(vec2(uv.x,i),angle.x);
        vec2 zr2=tiltPt(vec2(uv.y,zr.y),angle.y);
        vec3 z = vec3(zr.x,zr2.x,zr2.y);

        vec3 a = vec3(2.,2.,1.0);
        vec3 b = vec3(.5,.5,-1.0);

        for (int i = 0; i <250 && length(z)<10.; i++) {
            //z=vec3((4.0*z.z*z.z + z.y*z.y - 4.0*z.z*z.y),(z.x*z.x + 4.0*z.z*z.z - 4.0*z.z*z.x),(0.25*z.y*z.y + 0.25*z.x*z.x + 4.0*z.z*z.z + 0.5*z.y*z.x - 2.0*z.y*z.z -2.0*z.x*z.z));
            z = cross(z, a);
            z = cross(z, b);
            z =z*z+ vec3(zr.x,zr2.x,0.); 
        }
        if (length(z)<10.){col=.2+(sin(i-.6)+1.)/2.5;}}

    fragColor = vec4(col);
}
