// Green screen function from @iq 

// :D :D :D
vec4 compositeGreenScreenTexture(vec2 uv, vec4 background)
{

     vec4 tex = texture(iChannel0, uv);

     float maxrb = max( tex.r, tex.b );
     float k = clamp( (tex.g-maxrb)*5.0, 0.0, 1.0 );

     float ll = length( tex );
     tex.g = min( tex.g, maxrb*0.8 );
     tex = ll*normalize(tex);

     return mix(tex, background, k);

}

const mat2 r30 = mat2(0.8660254, -0.5000000,
                    0.5000000,  0.8660254);

float zebra(vec2 uv, float sharpness)
{
    float val = fract(uv.y);
    val = abs(val - 0.5);
    val = smoothstep(0.2, sharpness, val);
    return val;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float time = iTime * 0.05;
    float scale = 4.0;
    float sharpness = 0.47;
    vec2 uv = fragCoord/iResolution.y;
    vec2 uvg = uv + vec2(0, time);
    vec2 uvb = uv + vec2(0, -time);


    // Time varying pixel color
    float r = zebra(scale * uv * r30, sharpness);
    float g = zebra(scale *uvg * r30, sharpness);
    float b = zebra(scale *uvb * r30, sharpness);


    vec4 col = vec4(r, g, b, 1.0);

    col = compositeGreenScreenTexture(fragCoord/iResolution.xy, col);
    // Output to screen
    fragColor = col;
}
