void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 FC = (fragCoord/iResolution.xy-vec2(.3,.3))*2.;
    float t = iTime;

    vec2 r = vec2(.5*sin(t)+1.,1.);
    vec4 o=vec4(0.,0.,0.,0.);

    for(float i,z;i<1.;i+=.01){vec2 p=(FC.xy*2.-r)/r.y*i,v;p/=.2+sqrt(z=max(1.-dot(p,p),0.))*.3;p.y+=fract(ceil(p.x=p.x/.9+t)*.5)+t*.2;v=abs(fract(p)-.5);o+=vec4(2,3,5,1)/2e3*z/(abs(max(v.x*1.5+v,v+v).y-1.)+.1-i*.09);}o=tanh(o*o);

    // Output to screen
    fragColor = o;
}
