void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 v = texture(iChannel0, fragCoord/iResolution.xy).xxx * 0.5 + 0.5;
    fragColor = vec4(v, 1.0);
}

const vec3 LUM = vec3(0.3, 0.6, 0.1);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = fragCoord / iResolution.xy;

    if (iFrame == 0 || iMouse.z > 0.0) {
        fragColor = vec4(dot(texture(iChannel1, p).xyz, LUM) * 2.0 - 1.0, 0.0, 0.0, 0.0);
        return;
    }

    vec4 v = textureLod(iChannel0, p, 4.0);
    v.zw += vec2(dFdx(v.x), dFdy(v.x)) * -v.y;
    v.x = textureLod(iChannel0, p + v.zw / iResolution.xy, 0.0).x;
    v.y += v.x;
    fragColor = v;
}
