#define I iResolution
   precision lowp    float;

  const float PHI = 1.61803398874989484820459; // Φ = Golden Ratio 

  float gold_noise(in vec2 xy, in float seed)
  {
    return fract(tan(distance(xy*PHI, xy)*seed)*xy.x);
  }

void mainImage( out vec4 U, in vec2 V )
{
    vec2 u = V/I.xy;
    vec2 uv = V.xy / iResolution.xy;
    uv *=  1.0 - uv.yx;

    float seed = fract(3.141592654);                    // fractional base seed
    vec4 p       = vec4 (gold_noise(V, seed+0.2),  // r
                       gold_noise(V, seed+0.2),  // g
                       gold_noise(V, seed+0.2000001),  // b
                       gold_noise(V, seed+0.4)); // α


    float vig = uv.x*uv.y * 25.0; // multiply with sth for intensity

    vig = pow(vig, 0.15); // change pow for modifying the extend of the  vignette

    U += (0.9+p/10.0)-(1.1-vig);

}
