//BINARY STRINGS
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord*iResolution.xy;
    float lines = fract(cos(sin(float(int(uv.x * 0.02))) * (10. + iMouse.y)));
    float random = floor(0.5 + cos(tan(iTime * 2. + (iMouse.y * 0.00001) + (uv.y / lines))));
    float random2 = floor(0.5 + sin(cos(iTime * 2. + (iMouse.x * 0.00001) + (uv.x / lines))));
    fragColor = vec4(vec3(random) * vec3(0., 1., 0.) + vec3(random2) * vec3(0., 1., 0.),1.);
}
