vec4 noise( in vec2 xy, in float iTime ) {
  float len = length( xy );

  vec4 color;
  color.r = fract( tan( len*iTime+.1 ) * xy.x);
  color.g = fract( tan( len*iTime+.2 ) * xy.y);
  color.b = fract( tan( len*iTime+.3 ) * xy.x);
  color.a = fract( tan( len*iTime+.4 ) * xy.y);

  return color;
}

void mainImage( out vec4 fragColor, in vec2 xy ) {
  fragColor = noise( xy, iTime );
}
