vec2 new_v(vec2 v, int n, vec4 m) {
    for(int i = 0; i < n; i++) {
        v = vec2(v.x * m.x + v.y * m.z, v.x * m.y + v.y * m.w);
    }

    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec4 m = vec4(2, 1, 1, 1);

  vec2 uv = fragCoord/iResolution.xy; // Normalized pixel coordinates (from 0 to 1)

  vec2 new_uv = new_v(uv, int(iTime), m);
  vec4 col = texture(iChannel0, new_uv);

  fragColor = vec4(col); // Output to screen
}
