float hash12(vec2 p) {
	vec3 p3 = fract(p.xyx * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float noise(vec2 p) {
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u * u * (3.0 - 2.0 * u);
	float res = mix(
		mix(hash12(ip), hash12(ip + vec2(1, 0)), u.x),
		mix(hash12(ip + vec2(0, 1)), hash12(ip + vec2(1)), u.x), u.y);
	return res * res;	
}

float fbm(vec2 p, int octaves) {
	float s = 0.0, m = 0.0, a = 1.0;
	for(int i = 0; i < octaves; i++) {
		s += a * noise(p);
		m += a;
		a *= 0.65;
		p *= mat2(1.6, 1.2, -1.2, 1.6);
        p += s / a * 0.8;
	}
	return s / m;
}

vec3 hue_shift(vec3 col, float hue) {
    const vec3 k = vec3(0.57735);
    float cosa = cos(hue);
    return vec3(col * cosa + cross(k, col) * sin(hue) + k * dot(k, col) * (1.0 - cosa));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float mr = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / mr * vec2(0.7, 1);

    float nebula = fbm(uv * 3.0, 6);
    vec3 col = nebula * vec3(0.7, 0.1, 1);
    float smooth_nebula = fbm(uv * 4.0, 2);
    col += vec3(0.8, 1.5, 0.6) * dot(col, col) * smooth_nebula;

    vec2 rb = vec2(fbm(uv * 3.0 - 11.0, 2) * 0.2, sqrt(fbm(uv * 2.0 - 22.0, 4)) * 0.4);
    col.rb = col.rb * (1.0 + rb * sqrt(rb)) + rb * rb;
    float blue = fbm(uv * 2.5 - 55.0, 3);
    col = hue_shift(col, 0.1 - blue);
    col = mix(col.grb, col.brg, fbm(uv * 2.5 - 33.0, 4));

    fragColor = vec4(col, 1.0);
}
