void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0,0,0);

    //计算出中心点的位置
    vec2 tempUV = uv - vec2(0.5,0.5);

    //distance()函数：计算两个点之间的距离
    //这里求的是屏幕上每个点到屏幕中心点的距离
    float dis = distance(tempUV,vec2(0,0));

    //当像素点距离中心点越远，越靠近0.5，这里使用1-的方式取反
    //当距离中心点越远的地方，就越靠近0，所以越黑
    col = texture(iChannel0,uv).rgb*(0.5-dis);


    fragColor = vec4(col,1.0);
}
