void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    int numSections = 50;
    float sectionX = mod(uv.x, 1. / float(numSections)) * float(numSections);
    int sectionIndex = int(trunc(uv.x / (1. / float(numSections))));
    float instRandScale1 = tan(cos(uv.x * 100. + float(sectionIndex) + tan(float(sectionIndex) / 3.)));
    float instRandScale2 = tanh(1.3 * instRandScale1 + .3);

    float yFadeAnim = 1. + sin(iTime * 1. + instRandScale1) * 0.3;
    float tintAnim = 1. + sin(iTime * 1. + instRandScale1) * 0.2;

    float vertFade = pow(uv.y, 1.6 * yFadeAnim);
    vec3 tint = vec3(0.0, 0.1 + 0.2 * tintAnim, 0.8 * tintAnim);

    float sectionCenter = 1. * abs(sectionX - 0.5) + 0.7;
    vec3 col = tint * vertFade * sectionCenter;
    //col = vec3(yFadeAnim);
    fragColor = vec4(col,1.0);
}
