vec3 palette (float t) {
    vec3 a = vec3(0.968, 0.408, 0.798);
    vec3 b = vec3(0.659, 0.438, 0.328);
    vec3 c = vec3(0.388, 0.388, 0.296);
    vec3 d = vec3(1.500, 2.478, 0.168);

    return a + b * cos(6.28318 * (c * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    float mvt = cos(iTime * 1.25) * 5.0 / 5.0;

   // uv *= 0.5;
    uv = fract(uv + mvt * 0.25);
   // uv -= 0.5;

    float d = length(uv);

    vec3 col = palette(d + iTime);

    d = sin(d * 10.0 + mvt * 1.5) / 5.0;
    d = abs(d);
    // d = smoothstep(0.0, 0.05, d);
    d = 0.045 / d;

    col *= d;

    fragColor = vec4(col, 1.0);
}
