// Random white noise example

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize pixel coordinates
    vec2 uv = (fragCoord / iResolution.xy);

    vec3 col = vec3(rand());

    // Apply vignette effect
    col *= smoothstep(1.0, 0.1, length(uv - 0.5));

    // Output the final color
    fragColor = vec4(col, 1.0);
}

// Pseudo-random number generator
float rand(float seed) {
    return fract(sin(seed) * 43758.5453123);
}

// Generate a random value based on the pixel coordinates and time
float rand() {
    float a = iDate.w / 1000.0;
    float rsx = rand(gl_FragCoord.x + a);
    float rsy = rand(gl_FragCoord.y + rsx);
    float seed = a + rsy;
    return rand(seed);
}

vec2 mainSound( int samp, float time )
{
    return vec2(rand()*0.5);
}
