// ðŸ Cheap 4D Voronoi Marble (Equirectangular/Spherical)
// License CC0-1.0
//
// ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
// Technique inspired from FabriceNeyret2's Vorrocracks https://www.shadertoy.com/view/Xs3fR4
//
// Noise functions boosted up a dimension or two to create spherical/equirectangular texture.
// UV => Spherical coords and then searches for the closest voronoi cell edge in 4D and then 
// distorts with a "poor man's" fractional-brownian-motion.
// ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
//
// This is a cheaper cut-down version of the effect : https://www.shadertoy.com/view/43G3Wy
// Intended to be suitable for realtime-use as the material on a sphere or round object. 
// If you port this frag shader to a material, suggest you use true 3D obj vNormal instead of UV converted to spherical coords used for shader-toy

#define PI 3.1415926535897932384626433832795


bool darkmode = true;

int Seed = 70; // < Changes your current Voronoi Cell // Some of my favorite seeds [-1000]

vec3 Voronoi_Shape = vec3(.75), // 3D Shape/Scale of Voronoi Cells
     Movement_3D = vec3(0.,.4,.0); // The direction & speed of movement through XYZ of 4D voronoi space (Higher values = faster speed)

float Layers = 3., // Number of Layers
      Curve_Frequency = 2., // Higher Values = More Curves
      Smoothness = 1., // Higher Values = Smoother Curves
      Saturation = 1.1,     // Higher Values = More Saturated
      Line_Width = 30.,  //  Higher Values = Thinner Lines
      Line_Scale = 0., // Higher Values = bigger lines
      Fbm_Rotation = .37, // Z-Rotation applied on each "poor man's" FBM octave
      Layer_Rotation = .37, // Z-Rotation applied after each Layer pass
      ofs = .0001; // Alters 4D displacement 

// Voronoi Stuff


#define hash44(p) fract(18.5453 * sin(p * mat4(127.1, 311.7, 269.5, 183.3, 421.1, 547.6, 231.3, 298.4, 87.6, 123.4, 356.1, 654.3, 765.1, 876.4, 132.5, 234.8)))
#define disp(p) ( -ofs + (1.0 + 2.0 * ofs) * hash44(p) )


float voronoi4D_edge_distance(vec4 u) 
{
    // We triangle-wave loop through u.w which "breathes" our current Voronoi-Cell
    u.w *= 0.1;
    u.w = 1.0 - 2.0 * abs(fract(u.w + 0.5) - 0.5);
    u.w *= 0.99;


    u.w += float(Seed); // Apply current Voronoi-Cell Seed
    u.xyz += Movement_3D*iTime; // Apply XYZ Movement-Vector for 3D movement through the pattern

    // Get nearest 4D edge-distance 
    vec4 iu = floor(u);
    float m = 1e9, m2 = 1e9;
    vec4 P, P2;

    vec4 iu_offset = iu - 0.5;
    vec4 p, o, r;
    float d;

    // Loop over a 2x2x2x2 grid of cells around the current cell
    for (int k = 0; k < 6; k++) {
        p = iu_offset + vec4(k & 1, (k >> 1) & 1, (k >> 2) & 1, (k >> 3) & 1); // Using bitwise operations for faster modulus and division
        o = disp(p);  // Get the displacement for this cell
        r = p - u + o;
        d = dot(r, r);

        if (d < m) {
            m2 = m;
            P2 = P;
            m = d;
            P = r;
        } else if (d < m2 && dot(P - r, P - r) > 1e-5) {
            m2 = d;
            P2 = r;
        }
    }

    // Calculate the final value using the closest cells found
    if (m2 < 1e9) {
        m2 = 0.5 * dot((P + P2), normalize(P2 - P));
    }

    return m2;
}


// Brownian Stuff

// Hash function for 3D to 1D
#define hash31(p) fract(sin(dot(p, vec3(127.1, 311.7, 74.7))) * 43758.5453123)

// Noise function for 3D
float noise3(vec3 p) {
    vec3 i = floor(p);
    vec3 f = fract(p); f = f * f * (3.0 - 2.0 * f);

    float v = mix(
                mix(
                    mix(hash31(i + vec3(0, 0, 0)), hash31(i + vec3(1, 0, 0)), f.x),
                    mix(hash31(i + vec3(0, 1, 0)), hash31(i + vec3(1, 1, 0)), f.x), f.y),
                mix(
                    mix(hash31(i + vec3(0, 0, 1)), hash31(i + vec3(1, 0, 1)), f.x),
                    mix(hash31(i + vec3(0, 1, 1)), hash31(i + vec3(1, 1, 1)), f.x), f.y), f.z);

    return 1.0 - abs(2.0 * v - 1.0);
}


// Rotation matrix for 3D (rotation around z-axis)
mat3 rot3(float angle) {
    float c = cos(angle), s = sin(angle);
    return mat3(
        c, -s, 0,
        s,  c, 0,
        0,  0, 1
    );
}


// We bastardize FBM a bit here -- performance vs speed trade-off
vec3 poor_mans_fbm33(vec3 p) {
    vec3 v = vec3(0.0);
    float a = 0.5;
    mat3 R = rot3(Fbm_Rotation);
    for (int i = 0; i < 6; i++, p *= 2.0, a /= 2.0) {
        p = R * p;
        // Dirty trick to reduce noise-call to once per octave
        float n = noise3(p);
        float nn = n*n;
        v += a * vec3(n,nn,nn*n);
    }
    return v;
}




// UV coords to 3D space
vec3 uvToCartesian3D(vec2 uv) {
    // Convert UV to spherical coords
    float theta = uv.x * 2.0 * 3.14159265359; // Longitude
    float phi = uv.y * 3.14159265359; // Latitude
    // Convert Spherical Coords to 
    float x = sin(phi) * cos(theta);
    float y = sin(phi) * sin(theta);
    float z = cos(phi);
    return vec3(x, y, z);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    fragColor = vec4(0.,0.,0.,1.);
    vec2 uv = fragCoord/iResolution.xy; // Normalized pixel coordinates (from 0 to 1)

    // UV => 3D transformation
    vec3 pos = uvToCartesian3D(uv);


    // We move fbm out of loop for performance (slight detriment to the effect).
    vec3 f33=poor_mans_fbm33(pos/Smoothness);

    // For N Layers add curve/crack color, getting fainter and rotating input 3D vector on each pass.
    for(float i=0.; i<Layers ; i++) {
        vec3 V = pos/Voronoi_Shape;

        vec3 D = Curve_Frequency * f33 *Smoothness;

        float d = voronoi4D_edge_distance(vec4(V+D,iTime));

        d = min( 1., Line_Width * pow(max(0.,d-Line_Scale),Saturation) );

        fragColor += vec4(1.-d) / exp2(i);

        pos*= 1.5 * rot3(Layer_Rotation);
    }

    // If Darkmode
    if(!darkmode) fragColor = vec4(vec3(1.)-fragColor.xyz,1.);

}
