void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 iResolution = vec2(640.0, 360.0);
    vec2 iMouse = iMouse.xy / iResolution;

    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = uv * 2.0 - 1.0;
    p.x *= iResolution.x/iResolution.y;

    float t = iTime * 0.5;

    vec3 color = vec3(0.0);
    for (int i = 0; i < 100; i++)
    {
        float fi = float(i);
        vec2 pos = vec2(sin(fi + t * 0.1) * 0.3, cos(fi + t * 0.1) * 0.3);
        float d = length(p - pos);
        float size = 0.03 / d;
        color += vec3(size * sin(d * 20.0 + t), size * cos(d * 20.0 + t), size * sin(d * 10.0 + t));
    }

    for(int i = 0; i < 10; i++)
    {
        float fi = float(i);
        vec2 pos = vec2(sin(fi) * 0.9, cos(fi) * 0.9);
        pos += iMouse * 2.0 - 1.0;
        float d = length(p - pos);
        float size = 0.1 / d;
        color += vec3( size, size, size );
    }

    fragColor = vec4(color, 1.0);
}
