// Reference: https://www.shadertoy.com/view/4dl3zn

float sdCircle(vec2 circlePos, vec2 pos, float radius) {
    // 距离：正外负内
    return length(circlePos - pos) - radius;
    // 映射到[0,1]做渐变？
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy) / iResolution.y;

    float flag = -1.0;

    for( int i=0; i<40; i++) {
        // bubble seeds
		float pha =      sin(float(i)*546.13+1.0)*0.5 + 0.5;
		float siz = pow( sin(float(i)*651.74+5.0)*0.5 + 0.5, 4.0 );
		float pox =      sin(float(i)*321.55+4.1) * iResolution.x / iResolution.y;

        // bubble size, position and color
		float rad = 0.1 + 0.5*siz;
		vec2  cirPos = vec2( pox, -1.0-rad + (2.0+2.0*rad)*mod(pha+0.1*iTime*(0.2+0.8*siz),1.0));
        flag = flag * (step(0.,sdCircle(cirPos, uv, rad))*2. - 1.);


	}

    // vigneting	
	// color *= sqrt(1.5-0.5*length(uv));

    vec3 color = vec3(step(0.,flag));
	fragColor = vec4(color,1.0);




}
