const float STEP_SIZE = 0.5f;
const float MAX_DISTANCE = 10.f;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 ray = getRay(uv, iResolution.x / iResolution.y);

    float offset = getDitherValue(fragCoord) * STEP_SIZE;

    float result = 0.f;
    for(float d = offset; d <= MAX_DISTANCE; d += STEP_SIZE) {
        // Increase result if sample point is inside the bean
        result += getValue(ray * d, iTime) < 0.f ? STEP_SIZE : 0.f;
    }

    // Output to screen
    fragColor = vec4(vec3(result / MAX_DISTANCE), 1.0);
}

const float EPSILON = 0.001f;

const float VERTICAL_FIELD_OF_VIEW = 1.f;

vec3 getRay(in vec2 uv_position, in float aspect_ratio) {

    float vertical_offset = tan(VERTICAL_FIELD_OF_VIEW / 2.f);
    float horizontal_offset = vertical_offset * aspect_ratio;

    vec3 ray = vec3(
        horizontal_offset * (uv_position.x * 2.f - 1.f),
        vertical_offset * (uv_position.y * 2.f - 1.f),
        1.f
    );

    return normalize(ray);
}

const float ALPHA = 2.f;

float smoothMin(in float value_0, in float value_1) {
    return -log(exp(-value_0 * ALPHA) + exp(-value_1 * ALPHA)) / ALPHA;
}

const float RADIUS_0 = 1.f;
const float RADIUS_1 = 1.f;

const vec3 OFFSET = vec3(0.f, 0.f, -5.f);

float getValue(in vec3 position, float time) {
    vec3 center_0 = vec3(sin(time / 3.14f), 0.f, cos(time / 3.14f));
    vec3 center_1 = -vec3(sin(time / 3.14f), 0.f, cos(time / 3.14f));

    return smoothMin(distance(center_0, position + OFFSET) - RADIUS_0, distance(center_1, position + OFFSET) - RADIUS_1);
}

float getDitherValue(in vec2 fragCoord) {
    float result = 0.f;
    // 4x4 Bayer Pattern
    result += (mod(fragCoord.x, 2.f) - 0.f) * 8.f;
    result += (mod(fragCoord.y, 2.f) - 0.f) * 12.f;
    result += (mod(fragCoord.x / 2.f, 2.f) - 0.f) * 2.f;
    result += (mod(fragCoord.y / 2.f, 2.f) - 0.f) * 3.f;
    return mod(result, 16.f) / 16.f;
}
