void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_trn_nor = (fragCoord.xy - iResolution.xy*.5) /iResolution.yy;
    vec2 o_trn_nor_mouse = (iMouse.xy - iResolution.xy*.5) /iResolution.yy;

    vec4 o_bg = vec4(o_trn_nor.x+.3);//vec4(o_trn_nor.x*(1.+o_trn_nor.x));

    vec3 o_grey = vec3(0.486, 0.486, 0.486);
    //o_grey = vec3(0.486, 0.486, 0.486) + pow(max(0.0, fragCoord.x * 2. / iResolution.x - 1.), 2.) * .5;
    if(sign(iMouse.z) != 1.){
        o_trn_nor_mouse.x = (sin(iTime)*.5+.5)*.6;
    }
    vec2 o2 = abs(o_trn_nor.xy)-vec2(abs(o_trn_nor_mouse.x), 0.);
    float n = 1.-smoothstep(0.1, 0.11, max(abs(o2.y), abs(o2.x))*length(o2)*6.);
    vec4 o_shape = vec4(n*o_grey, 1.);
    fragColor = o_shape+(1.-n)*o_bg;
    if(sign(iMouse.z) == 1.){
        fragColor *= vec4(1.-n*float(o_trn_nor.y>0.06));
        fragColor += 1.-vec4(1.-n*float((o_trn_nor.y*-1.)>0.06));
    }
}
