void mainImage( out vec4 O, vec2 U ){

    vec2 r = iResolution.xy;
    vec2 uv = U/r;
    vec2 cuv = (2.*U-r)/r.y;
    vec2 muv = (2.*iMouse.xy-r)/r.y;

    O = vec4(0);
    float a = aValue;
    for (int i = -int(a*pi); i < int(a*pi); i++){
        O += texture(iChannel0, uv + vec2(0,float(i))/r)*blur(float(i), a, sValue);

    }

    O = pow(O, vec4(1./2.2));
}

#define pi 3.14159265
#define phi 1.6180339887
#define e 2.71828182846

#define aValue (iMouse.z>0.?iMouse.x/5.:cos(iTime*2.)*40.+50.)


#define sValue 1
// 1 : Gaussian
// 2 : Box
// 3 : Cone
// 4 : Cursed
// 5 : Dome
// 6 : Smeary - I used to think this was a gaussian lol

float blur(float x, float a, int s){
    switch(s){
        case 1:
            a/=pi;
            return pow(e, -.5*(x/a)*(x/a))/(a*sqrt(2.*pi)); 
        case 2:
            return abs(x)>=a?0.:.5/a;
        case 3:
            return max(0., (1.-abs(x/a))/a );
        case 4:
            return abs(x)>=a?0.:(x/a)*(x/a)/a;
        case 5:
            return abs(x)>=a?0.:sqrt(1.-x*x/a/a)/pi*2./a;
        case 6:
            a /= 8.;
            return 1./(a*pi)/(1.+(x/a)*(x/a));
    }


}

