// Fork of "better template" by ProfessorPixels. https://shadertoy.com/view/l3yGRV
// 2024-06-12 01:05:53
float sdfSquare(vec2 p,float a,float m)
{
    p=abs(p);
    a-=m+m;
    float d = 0.;
    if (min(p.x,p.y)<a) d= max(p.x,p.y)-a;
    else d= length(p-a);
    d-=m+m;
    return d;
}
void mainImage(out vec4 O,vec2 F )
{
    F-=iResolution.xy*.5;
    F*=.2;
    float d = sdfSquare(F,20.,5.);
    if (d<0.) O=vec4(.1,.7,1.,0)*(cos(6.3*d)*.25+.75);
    else O=vec4(1.,.7,.1,0)*(cos(6.3*d)*.25+.75);
    O*=clamp(2.*abs(d),0.,1.);
}
