float Pow2(float a) {
    return a*a;
}
#define DIVISOR 8192.0
#define INCREMENT 1024.0
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec4 color = texture(iChannel0, uv);
    float time = fract(iTime/64.0);
    for(float i = 1.0; i <= DIVISOR; i+=INCREMENT) {
        float r = ((1.0/(DIVISOR/2.0))*i)-time;
        for(float y = 0.0; y <= 1.0; y += 0.25) {
            for(float x = 0.0; x <= 1.0; x += 0.25) {
                if(sqrt(Pow2(uv.x-x)+Pow2(uv.y-y)) < r) {
                    color = 1.0 - color;
                }
            }
        }
    }
    fragColor = vec4(color);
}
