void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float timeX = -mod(iTime, 30.0);
    vec2 offset_01 = vec2(timeX/10.0) + vec2(0.079, 0.070);

    // Aplica el desplazamiento a las coordenadas UV
    vec2 uvOffset = uv + offset_01;

    // Muestra la textura con las coordenadas UV desplazadas y la salida a la pantalla
    fragColor = texture(iChannel0, uvOffset);
}
