void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - vec2(0.5, 0.5);

    vec2 begin = vec2(1.0,-0.71);
    vec2 end = vec2(-0.5, -0.7);

    vec3 a = vec3(begin, 0.0);
    vec3 b = vec3(uv, 0.0);
    vec3 c = vec3(end, 0.0);


    vec3 ba = cross(b, a);
    vec3 cb = cross(c, b);
    vec3 ca = cross(c, a);
    bool mask;
    if (ca.z >= 0.0) {
        mask = ba.z >= 0.0 && cb.z >= 0.0;
    } else {
        mask = ba.z >= 0.0 || cb.z >= 0.0;
    }
    fragColor = mask ? vec4(1, 0, 0, 1) : vec4(1, 1, 1, 1);
}
