// version of
// https://www.shadertoy.com/view/mddyWS

#define r(a) mat2(cos(a + vec4(0, 33, 11, 0)))

void mainImage(out vec4 O, vec2 u) {
    O *= 0.;
    vec3  p = iResolution; 
          u = (u + u - p.xy) / p.y;  

    float c, i, d, a, T = iTime * .5;

    while(i++ < 120.){
        p = .6 * vec3(u * a, a - .3);

        p.yx *= r(T * 1.2);
        p.yz *= r(T * 1.3); 

        p *= d = 1.8 / dot(p, p);

        p.xz *= r(p.y *.1);

        int j; 
        while(j++ < 6) 
            p.z = abs(p.z), 
            p.xz *= r(3.14/6.);

        c = abs(cos(p.y)) / 544. + abs(p.y) / 7000.;
        p = abs(sin(p)) * cos(p * p.z / 20.) * 9. + 2.;

        a += d = .12 * p.y / d;

        O += vec2( .01 / exp(2000. * sqrt(abs(d)) + .9), 
                   (.01 - c) / exp(1e5 * d + .7)

             ).xyxx * 1.4;
    }
}
