vec3 erot(vec3 p,vec3 ax,float t){return mix(dot(ax,p)*ax,p,cos(t))+cross(ax,p)*sin(t);}
float box3(vec3 p,vec3 b){p=abs(p)-b;return length(max(vec3(0.),p))+min(0.,max(p.x,max(p.y,p.z)));}
struct Grid {
    float d;
    vec3 id;
};
vec3 pcg3d(vec3 p){
    uvec3 q=floatBitsToUint(p)*1234567u+123456789u;
    q.x+=q.y*q.z;q.y+=q.x*q.z;q.z+=q.y*q.x;
    q^=q>>16;
        q.x+=q.y*q.z;q.y+=q.x*q.z;q.z+=q.y*q.x;
        return vec3(q)/(float(-1u));
}
Grid dogrid(vec3 ro,vec3 rd,float size){

    Grid g;
    g.id = floor((ro+rd*.001)/size+.5)*size;
    vec3 src = -(ro-g.id)/rd;
    src+= abs(.5*size/rd);
    g.d  = min(src.x,min(src.y,src.z));
    return g;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy -.5* iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.);

    vec3 ro=vec3(.25,.2,-0.),rt=vec3(0.,0,5.);
    //rt=erot(rt,vec3(0.,1.,0.),iTime*.1);
    vec3 rnd = pcg3d(uv.xyy);
    float t = iTime*170./60.+smoothstep(0.,.75,sqrt(length(uv.y))-.35)*rnd.x*.3;

    ro.z+=t;
    ro.x +=sin(t*.33);
     ro.y +=cos(t*.5);
    rt.z+=t;
        rt.x -=cos(t*.66);
     rt.y -=sin(t*.125);
    vec3 z = normalize(rt-ro),x=vec3(z.z,0.,-z.x),y=cross(z,x);
    vec3 rd = mat3(x,y,z)*normalize(vec3(uv,.2));
    rd = erot(rd,vec3(0.,0.,1.),tanh(cos(t*.2+rnd.z*.01)*10.)*3.14);
    vec3 p=ro;
    float i=0.,e=0.,g=0.;
    float len = 0.;
    Grid gr;
    for(i=0.;i++<50.;){
        if(len<=g){
            gr=dogrid(p,rd,1.);
            len+=gr.d;
        }
        vec3 r = pcg3d(gr.id);

      vec3 op=p;
        p-=gr.id;
        vec3 oop=p;
        p=erot(p,normalize(r-.5),length(r*r)*6.28+iTime);
        p=abs(p)-.25;
        p*=4.;
        p=abs(p)-.125;
        p*=2.;

        float h = box3(p,vec3(.125));
        float z = min(length(oop.xy),min(length(oop.zy),length(oop.xz)))-.01;
        e=max(0.001,min(abs(z),h/16.));
        g=min(g+e,len);
        p = ro+g*rd;
        col+=vec3(1.0,.5,.2)*(0.075+.1*exp(-3.*fract(length(op)+t)))/exp(i*i*e);
    }

    fragColor = vec4(col,1.0);
}
