#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float a,i,j,d=.1,e,n=2.09; vec3 p,r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    p.z-=8.;
    p.yz*=R2D(3.2+iTime*.1);

    for(j=0.;j++<8.;){
        p=abs(abs(p))-.6;
        a=atan(p.x,p.y)*3.14;
        d=cos(floor(.5+a/n)*n-a)*length(p)-.2;
        //d-=dot(p,p)/2e4;
    }e+=d;
}//o+=3./i;
o+=vec4(sqrt(p*vec3(-1,-1,2)),1)*8./i;
}
