// shaders lindos ♥ https://www.shadertoy.com/playlist/cXBGzV

#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define t iTime * .75

void mainImage(out vec4 o, vec2 u) {    
    vec2 r = iResolution.xy; 
         u = (u - r.xy / 2.) / r.y;

    o *= 0.;
    vec3 D = normalize(vec3(u, 2)), f, k,
         p = vec3(
                 sin(t * .78) * 1. - 1.,
                 cos(t) * .2, 
                 t * 2.
             );

    k = vec3(
            cos(2. * t) * 2.,
            sin(3. * t) * 1. - 1., 
            sin(4. * t) * 2. + 15.
        ) * .2 + p;

    D.zy *= rot(sin(t) * .2 + .1);
    D.zx *= rot(cos(t * .1) * .2);

    float i, j, e, d = 1., 
          agua, nivelDagua, nivel,
          ball, solo, sujeira;

    while(i++ < 100. && d > .01){
        nivel = p.y 
                    + tanh(cos(t * .4) * 4.) * .4 
                    + .1 
                    + cos(length(p.x) * 3. + iTime * 2.) * .1;

        ball = length(p - k) - .1;
        agua = abs(nivel + .1) - .02;
        nivelDagua = nivel + .18;
        sujeira = max(.0, nivelDagua);
        solo = -abs(p.y - .4) + 1.2;     //teto = -p.y + .8;


        j = 1.;
        e = 0.;
        while(j < 65.)
            j += j,
            f = p * j * 3.,
            f.y += cos(t) * j * .9, 
            f.yz *= rot(j),
            e += abs(dot(sin(f), vec3(1))) / j;

        d = min(
                ball,
                min(
                    agua, 
                    min(solo, sujeira)
                ) + e * .09 
            );

        p += d * D;
    }

    j = 2.;
    o += j / (length(p - k) - .1 + j);

    if(d > nivelDagua)
        o.b *= (length(o.rg) * 1. + 1.) * .5,
        o.rgb -= cos(t) * .2;

    else
        o.g += .2;


    o = o*o*o;
}
