vec3 rotateVector(vec3 v, vec3 axis, float angle) {
    return v * cos(angle) +
           cross(axis, v) * sin(angle) +
           axis * dot(axis, v) * (1.0 - cos(angle));
}

mat2 rot2D(float angle)
{
    float s = sin(angle);
    float c = cos(angle);
    return mat2(c, -s, s, c);
}

float smin( float a, float b, float k )
{
    k *= 4.0;
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*k*(1.0/4.0);
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}


float sdBoxFrame( vec3 p, vec3 b, float e )
{
       p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

//distance to scene function
float map(vec3 p, vec3 move)
{
    float scale = 4.;


    vec3 q = p; //input point copy

    q.xz = fract(p.xz) - 0.5;//repeat


    //SRT (TRS)

    vec3 torusXform = rotateVector(q, vec3(0, 0, 1), cos(iTime * 2.));

    float torus = sdTorus(torusXform * 2.5, vec2(1.1, 0.2)) / 2.5;

    //vec3 cubeXform = rotateVector(p * scale, normalize(vec3(1, 0, 1)), sin(iTime)) + move;
    //vec3 cubeXform = rotateVector(q * 3., normalize(vec3(1, 0, 1)), sin(iTime) * 5.) + move;
    vec3 cubeXform = rotateVector(q, normalize(vec3(1, 0, 1)), sin(iTime) * 5.);
    //space needs to be scaled down so it fits within the repitition space(?)
    float cube = sdBox(cubeXform * 5., vec3(0.5, 0.5, 0.5)) / 3.; //dividing by scale removes artifacts. scaling distorts metrics in scene(?)


    float boxFrame = sdBoxFrame(q, vec3(0.5, 0.5, 0.5), 0.04);


    float sphere = sdSphere(p, 0.6);

    //float ground = p.y + 0.75;


    //float un = smin(ground, smin(cube, torus, 0.3), 0.1);
    float un = smin(cube, torus, 0.1);
    return un;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;
    vec2 m = (iMouse.xy * 2. - iResolution.xy) / iResolution.y;

    //Initialisation
    //define ray origin at z -3 in world 
    vec3 ro = vec3(0, 0, -3);
    //field of view
    float fov = 0.5;
    //ray direction
    vec3 rd = normalize(vec3(uv * fov, 1)); //uses uv to determine direction for each pixel
    //keeps track of distance travelled by ray across the world
    float t = 0.;
    vec3 col = vec3(0);

    vec3 translation = vec3(sin(iTime) * 5., cos(iTime) * .2, 0);

    //vertical cam rotation
    ro.yz *= rot2D(-m.y);
    rd.yz *= rot2D(-m.y);

    //horizontal cam rotation
    ro.xz *= rot2D(-m.x);
    rd.xz *= rot2D(-m.x);



    //Raymarching
    //current position along ray
    for(int i = 0; i < 200; i++)
    {
        vec3 p = ro + rd * t;   //marches ray: ray origin + the direction
        float d = map(p, translation);   //get distance to closest point from p
        t += d; //assigns distance travelled to total distance

        if(d < 0.001f) break;   //stop if ray gets too close to object
        if(t > 1000.) break; //stop if ray goes too far

    }

    col = vec3(t * .2); //sets colour to 'depth buffer'


    fragColor = vec4(col, 1);
}
