void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv = fragCoord/iResolution.xy;
    vec4 color = vec4(0.0);
	float seg = 5.;
	float i = -seg;
	float j = 0.;
	float f = 0.0f;
	// float dv = 2.0f/512.0f;

    float vluae = 2.;

    float dvx = vluae/iResolution.x;
    float dvy = vluae/iResolution.y;

	float tot = 0.0f;
	for(; i <= seg; ++i)
	{
		for(j = -seg; j <= seg; ++j)
		{
			f = (1.1 - sqrt(i*i + j*j)/8.0);
			f *= f;
			tot += f;
			color += texture( iChannel0, vec2(uv.x + j * dvx, uv.y + i * dvy) ).rgba * f;
		}
	}
	color /= tot;
	fragColor = color;



    // Output to screen
    //fragColor = vec4(col,1.0);
}
