#define PI 3.1415

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const vec3 bg = vec3(1.0);
    const vec3 fg = vec3(0.78, 0.09, 0.75);
    const vec3 oof = vec3(0.413, 0.789, 0.19);
    const vec3 deepblue = vec3(0.15, 0.19, 0.39);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float fact = mix(abs(cos(-iTime + uv.x * PI)),
                     abs(sin(-iTime + uv.y * PI)),
                     0.61);
    fact = max(0.0, clamp(fact/tan((iTime + uv.y + uv.x)*PI), 0.0, 1.0))
         + max(0.0, clamp(-fact/sin((iTime + uv.y + uv.x)*PI), 0.0, 1.0))
         + mix(fact, 0.61616161, sin(uv.x * PI));
    vec3 col = mix(bg, fg, fact);
    col = mix(col, oof, cos(fact * PI));
    col = mix(col, oof, clamp(tan(4.0 * fact * PI), 0.0, 1.0));
    col = mix(col, deepblue, clamp(tan(PI * (1.0 - fact)), 0.3, 0.9));

    col = clamp(col + 0.2, 0.0, 1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
