void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize pixel coordinates
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Calculate rotation angle (45 degrees every second)
    float currentAngle = radians(45.0 * floor(iTime));
    float prevAngle = radians(45.0 * floor(iTime - 1.0));

    // Rotate UV coordinates for current and previous positions
    vec2 currentUV, prevUV;
    float c = cos(currentAngle);
    float s = sin(currentAngle);
    currentUV = vec2(uv.x * c - uv.y * s, uv.x * s + uv.y * c);

    c = cos(prevAngle);
    s = sin(prevAngle);
    prevUV = vec2(uv.x * c - uv.y * s, uv.x * s + uv.y * c);

    // Define square
    float squareSize = 0.15;
    vec2 currentSq = abs(currentUV);
    vec2 prevSq = abs(prevUV);
    float currentSquare = float(max(currentSq.x, currentSq.y) < squareSize);
    float prevSquare = float(max(prevSq.x, prevSq.y) < squareSize);

    // Calculate fade factor for trail (quicker fade)
    float fadeFactor = smoothstep(0.0, 0.25, fract(iTime));
    float trail = prevSquare * (1.0 - fadeFactor);

    // Combine current square and trail
    float finalSquare = max(currentSquare, trail);

    // Set color (white square on black background)
    vec3 color = vec3(finalSquare);

    // Output to screen
    fragColor = vec4(color, 1.0);
}
