#define rot(x) mat2(cos(x+vec4(0,11,33,0)))

//formula for creating colors;
#define H2(h)  (  cos(  h + 5.*vec3(1,2,0)   )*.5 + .4 )

//formula for mapping scale factor 
#define M(c)  log(1.+c)

#define R iResolution


void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0);
    vec4 rd = normalize( vec4(U-.5*R.xy, .5*R.y, R.y*4.))*200.;

    float param = mod(iTime,10.)<5.?.2:.15;

    float sc,dotp,totdist=0., tt=iTime/3., t=iTime;

    for (float i=0.; i<100.; i++) {

        vec4 p = vec4( rd*totdist);

        p.xyz += vec3(700.*sin(tt),0,-1200.+500.*cos(tt)); 

        p.yzw = p.xyz;

        sc = 1.; 

        p.xz *= rot(tt*1.7);
        p.xw *= rot(tt*1.3);

        vec4 w = p;

        for (float j=0.; j<7.; j++) {

            p = abs(p)*.65;


            dotp = max(1./dot(w,w),.1);
            sc *= dotp*1.1; 

            p = p * dotp - .55; 

            w = .8*log(1.-abs(p)*p*p) - .2;  //switched sign

        }

        float dist = abs( length(p)-.1)/sc ;  //funky distance estimate
        float stepsize = dist/10. ;     
        totdist += stepsize;                  //move the distance along rd

        //only using final angle for color mapping
        c +=
             .04* H2(  atan( p.x, p.w )) * exp(-totdist*8e-2);
    }

    c = 1. - exp(-c*c);
    O = ( vec4(c,0) );

}
