float random(float x){
    return fract(sin(x+0.5)*8.5);
}



float randomS(float x){
    return sin(3.1415926535*x)*cos((x+iTime*.2) * 40.0) *0.2 +.5;
}

float randomL(float x){
    return fract(sin(x+0.5)*25000.5);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min(iResolution.y, iResolution.x);
    //uv = uv* 2. - 1.;
    vec2 lightpos = vec2(0,0);
    vec2 pos2uv = uv-lightpos;




    float blocksize = 500.0;
    vec2 digital = (uv + 1.0)/2.0;
    digital *= blocksize;
    digital = floor(digital);
    float write = digital.x + digital.y * blocksize;
    write /= blocksize*blocksize;



    float len = length(pos2uv);

    len += randomS(write)*0.02;
    len += randomS(len)*0.04;
    float rlen = len + random(len);
    float col = (1.0-len)*rlen;

    float r = fract(sin(1.0)*8.5);
    float add = len/r;
    col += 1.0-clamp(add,0.0,1.0);





    fragColor = vec4(col,col,col,1.0);
}
