void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;

    // 音声データを取得
    float audioData = texture(iChannel0, vec2(uv.x, 0.0)).r;

    // 中心からの距離を計算
    float center = abs(uv.y - 0.5);

    // 音声の振幅に応じて円の大きさを変える
    float circle = smoothstep(0.1 + audioData * 0.2, 0.0, center);

    // 出力（音に反応する円）
    fragColor = vec4(vec3(circle), 1.0);
}
