// 20240926
#define  E(a) fract(234.567*sin(2.+(a)*123.456))

void mainImage(out vec4 O, vec2 U){
        float N= 25.,i,j,t=iTime,T;
        vec2 R = iResolution.xy,
             p = N*(U+U-R) / R.y,w,v ;

        i = round(p.x);                
        p.y += T = fract(t+E(i));        
        w =vec2(19,-19)+2.*sin(i / N * vec2(18,6)+t*2.);
        v = round(p.y-w)+w;                         
        j= p.y<0. ?v.y:v.x;        
        if(j>=w.y  && j<=w.x)j = max(w.y, w.x - 50. * T * T);        
        O = vec4(.4,p.y+1.,-1,1) + smoothstep(3.*N/R.y,  0., length(p - vec2(i, j))-.7);
}
