#define PI 3.1415926538

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 borders = step(vec2(0.1), uv) - step(vec2(0.9), uv);
    float isBorder = borders.x * borders.y;

    float steps = abs(cos(iTime/5.)/PI + 0.9) * 20.;
    float offset = mod(iTime, 10.) / 10.;
    float isStep = step(uv.x + offset, floor((uv.y+offset)*steps)/steps);

    fragColor = vec4(vec3(isBorder - isStep), 1.0);
}
