// 2024-6-2
void mainImage( out vec4 O, vec2 U )
{
        vec2 R = iResolution.xy,
             u = 2.*( U+U - R ) / R.y,
             e = normalize(sqrt(2.)-vec2(0,2));//,

        u -= round(u);
        u =abs(u);
        u.x<u.y?u=u.yx:u;
        u.x-=.5;
        -u.x<u.y?u=-u.yx:u;
        u.x+=.25;
        dot(u,e)<0.?u=reflect(u,e):u;
        u.x=min(u.x,0.);
        O.x= exp(-20.*length(u));
}
