#define PI 3.14159265359

// ColorA and ColorB
vec3 Color_blue = vec3(0.222,0.413,0.912);
vec3 Color_green = vec3(0.518,1.000,0.612);
vec3 Color_pink = vec3(0.973,0.749,1.000);

float plot(vec2 st, float pct){
  return  smoothstep( pct-0.1, pct, st.y) -
          smoothstep( pct, pct+0.1, st.y);
}


vec3 pattern(vec2 c, vec2 p)
{
	float ang = atan(p.y-c.y,p.x-c.x);
	float d = distance(c,p);
	//float rd = d;
	//if (rd < 0.04) return vec3(1.,1.,1.);
	//d = 0.05/(d*d) + iTime;


	if (mod(d,0.5) <= 0.2) {
		ang += PI/20.;	
	}

	if (mod(d,1.0) <= 0.5) return vec3(1.0,1.0,1.0);

	if (mod(d,2.0) <= 1.0) ang += iTime*0.4;
	else ang -= iTime*0.4;

	if ( mod(ang ,PI/10.) < PI/20.) return vec3(1.0,1.0,1.0);

	return vec3(sin(d+sin(iTime)),cos(d+cos(iTime)),sin(iTime)-cos(iTime));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;

    vec3 pct_y = vec3(st.y);
    vec3 pct_x = vec3(st.x);
    vec3 Color1 = mix(Color_green, Color_blue, pct_y + sin(iTime));
    vec3 Color2 = mix(Color1, Color_green, pct_y + pct_x + cos(iTime));

    vec3 pct = vec3(st.y) * smoothstep(0.0, 0.4, st.x) * sin(iTime);
    vec3 Color3 = mix(Color_pink, Color1, pct);

    vec3 Color = mix(Color3, Color_blue, clamp(pct_x*st.y, 0.0, 0.6));


	float ang = atan(st.y,st.x);
	float d = st.x;

	if (mod(d,0.5) <= 0.2) {
		ang += PI/20.;	
	}

	if (mod(d,1.0) <= 0.5) Color = Color3;

	//if (mod(d,2.0) <= 1.0) ang += iTime*0.3;
	//else ang -= iTime*0.3;

	if ( mod(ang ,PI/10.) < PI/20.) Color = Color2;




    // Output to screen
    fragColor = vec4(Color,1.0);
}
