//（图片uv，相对于最小帧的当前帧，最小帧，最大帧，序列图列数，序列帧行数）https://www.shadertoy.com/view/4XVSDc
vec2 SequenceUV(vec2 uv,float nowNum,float minNum,float maxNum,float colNum,float rowNum)
{
    nowNum = mod(floor(nowNum),abs(maxNum - minNum))+ min(minNum,maxNum);
    uv.x = uv.x/colNum + nowNum /colNum;
    uv.y = uv.y/rowNum + (rowNum - floor(nowNum/colNum)  - 1.)/rowNum;
    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 catUV = SequenceUV(uv,iTime*6.,0.,5.,6.4,1.);//猫的序列图右边有0.4空白
    vec3 color = texture(iChannel0,catUV).rgb;

    // Output to screen
    fragColor = vec4(color,1.0);
}
