vec2 rotate(vec2 p, float a)
{
	mat2 m = mat2(cos(a), -sin(a), sin(a), cos(a));
	return p * m;
}

// https://iquilezles.org/articles/palettes/
vec3 palette( float t ) 
{
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.0,0.10,0.20);

    return a + b*cos( 6.28318*(c*t+d) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 p = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.xy + 0.2;

	p = vec2(length(p), sin(p.y));

	p = mix(p * 0.2, p, mouse.x);

	vec2 p1 = mix(p, tan(p * 10.0), mouse.y * 0.04);

	vec2 t = mod(vec2(0.4 * p1.x, p.y) * 2.0, rotate(p1, tan(p.y) + iTime));
    float t1 = t.x / t.y;

    t1 = t1 / length(p * 8.0);

    float t2 = step(mod(p.x, abs(sin(iTime * 0.8))) + 0.2, t1);
    t1 = smoothstep(0.0, abs(sin(iTime * 0.4)) + 0.4, t1);

    t1 = mix(t1, t2, length(p));

    vec3 col = palette(t1 + 0.4);

    fragColor = vec4(col,1.0);
}
