vec3 grid(in vec2 q){
    vec3 C = vec3(0.0);
    for(float j = 0.0; j < 7.0; j += 1.0){
		vec2 r = q - 0.5;

		C += pow(dot(r, r.yx), 2.0);

		q = fract(q * 2.0);
    }
    return C;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    fragColor = vec4(grid(fract(uv * 4.0)),1.0);
}
