float d2(vec2 v, vec2 o) {
    vec2 d = v-o;
    return dot(d,d);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
vec3 theta = vec3(-2.0,0.0,2.0)+iTime;
float rad = 0.5;
vec3 rgbx = cos(theta);
vec3 rgby = sin(theta);
vec2 rpos = rad*vec2(rgbx.r,rgby.r);
vec2 gpos = rad*vec2(rgbx.g,rgby.g);
vec2 bpos = rad*vec2(rgbx.b,rgby.b);

    vec2 p0 = 2.0*fragCoord/iResolution.y-1.0;
    vec3 d2 = vec3(d2(rpos,p0),d2(gpos,p0),d2(bpos,p0));
    float zoom = (6.0-4.0*cos(iTime)*cos(iTime/2.0));
    d2 = vec3(uvec3(1024)>>uvec3(d2*zoom));
    d2 /= 1024.0;
    vec3 col = d2;
    fragColor = vec4(col,1.0);
}
